import React, { useState } from "react";
import { useEffect } from "react";
import { useDispatch } from "react-redux";
import { filterBy } from "../redux/actions";

export default function Filters({ genres, handlePage }) {
  const dispatch = useDispatch();
  const [filterGames, setFilterGames] = useState({
    genre: "All",
    created: "All",
    param: "name",
    type: "ASC",
  });

  useEffect(() => {
    dispatch(filterBy(filterGames));
  }, [filterGames, dispatch]);

  function handleSelect(e) {
    const values = { ...filterGames, [e.target.name]: e.target.value };
    setFilterGames(values);
    handlePage(1);
  }

  return (
    <div>
      <div>
        <select name={"genre"} onChange={(e) => handleSelect(e)}>
          <option key={"All"} value={"All"}>
            All
          </option>
          {genres.length
            ? genres.map((g) => {
                return (
                  <option key={g.id} value={g.name}>
                    {g.name}
                  </option>
                );
              })
            : null}
        </select>
        <select name={"created"} onChange={(e) => handleSelect(e)}>
          <option value="All">All</option>
          <option value="api">Api results</option>
          <option value="db">Db results</option>
        </select>
      </div>
      <div>
        <select name={"param"} onChange={(e) => handleSelect(e)}>
          <option value="name">Alphabetical</option>
          <option value="rating">Rating</option>
        </select>
        <select name={"type"} onChange={(e) => handleSelect(e)}>
          <option value="ASC">Ascendant</option>
          <option value="DESC">Descendant</option>
        </select>
      </div>
    </div>
  );
}
