import React, { useState } from "react";
import { useEffect } from "react";
import { useDispatch } from "react-redux";
import { filterBy, sortGames } from "../../redux/actions";

export default function Filters({ genres, handlePage }) {
  const dispatch = useDispatch();
  const [filterValues, setFilterValues] = useState({
    genre: "All",
    created: "All",
  });
  const [sortValues, setSortValues] = useState({
    param: "name",
    type: "ASC",
  });

  // useEffect(() => {
  //   dispatch(filterBy(filterValues));
  //   dispatch(sortGames(sortValues));
  // }, [dispatch]);

  // useEffect(() => {
  //   dispatch(sortGames(sortValues));
  // }, [sortValues, dispatch]);

  function handleSelectFilter(e) {
    const values = { ...filterValues, [e.target.name]: e.target.value };
    setFilterValues(values);
    dispatch(filterBy(filterValues));
    handlePage(1);
  }

  function handleSelectSort(e) {
    const values = { ...sortValues, [e.target.name]: e.target.value };
    setSortValues(values);
    dispatch(sortGames(sortValues));
    handlePage(1);
  }

  return (
    <div>
      <div>
        <select name={"genre"} onChange={(e) => handleSelectFilter(e)}>
          <option key={"All"} value={"All"}>
            All
          </option>
          {genres.length
            ? genres.map((g) => {
                return (
                  <option key={g.id} value={g.name}>
                    {g.name}
                  </option>
                );
              })
            : null}
        </select>
        <select name="created" onChange={(e) => handleSelectFilter(e)}>
          <option value="All">All</option>
          <option value="api">Api results</option>
          <option value="db">Db results</option>
        </select>
      </div>
      <div>
        <select name="param" onChange={(e) => handleSelectSort(e)}>
          <option value="name">Alphabetical</option>
          <option value="rating">Rating</option>
        </select>
        <select name="type" onChange={(e) => handleSelectSort(e)}>
          <option value="ASC">Ascendant</option>
          <option value="DESC">Descendant</option>
        </select>
      </div>
    </div>
  );
}
