import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useHistory } from "react-router-dom";
import { createGame, getAllGenres, getAllPlatforms } from "../../redux/actions";
import { validateForm } from "../common/utils";
import "../styles/createGame.css";
import ListSelect from "./Datalists";
import SelectedList from "./SelectedList";

export default function CreateGame() {
  const dispatch = useDispatch();
  const history = useHistory();
  const genres = useSelector((state) => state.genres);
  const platforms = useSelector((state) => state.platforms);
  const response = useSelector((state) => state.response);
  const [errors, setErrors] = useState({});
  const [game, setGame] = useState({
    name: "",
    description: "",
    released: "",
    rating: 0,
    image: "",
    platforms: [],
    genres: [],
  });

  useEffect(() => {
    dispatch(getAllGenres());
    dispatch(getAllPlatforms());
  }, [dispatch]);

  useEffect(() => {
    setErrors(validateForm(game));
  }, [game]);

  function handleChange(e) {
    const value = { ...game, [e.target.name]: e.target.value };
    setGame(value);
  }

  function handleList(v, name) {
    const value = { ...game, [name]: [...game[name], v] };
    setGame(value);
  }
  function deleteFromList(v, name) {
    const newList = game[name].filter((e) => e !== v);
    const value = { ...game, [name]: newList };
    setGame(value);
  }

  function submitCreation(e) {
    e.preventDefault();
    dispatch(createGame(game));
    history.push("/games");
  }

  return (
    <div>
      <form className="form">
        <label>
          <input
            type="text"
            name="name"
            placeholder="Name"
            onChange={handleChange}
          />
          {errors.name ? <p>{errors.name}</p> : null}
        </label>
        <label>
          Description
          <input type="text" name="description" onChange={handleChange} />
          {errors.description ? <p>{errors.description}</p> : null}
        </label>
        <label>
          Date of release
          <input type="date" name="released" onChange={handleChange} />
          {errors.released ? <p>{errors.released}</p> : null}
        </label>
        <label>
          Rating
          <input type="number" name="rating" onChange={handleChange} />
          {errors.rating ? <p>{errors.rating}</p> : null}
        </label>
        <label>
          Image:
          <input type="text" name="image" onChange={handleChange} />
          {errors.image ? <p>{errors.image}</p> : null}
        </label>
        <label>
          Select genres for this game:
          <ListSelect
            name="genres"
            list={genres.map((g) => g.name)}
            onSelect={(v) => {
              handleList(v, "genres");
            }}
          />
          {game.genres ? (
            <SelectedList
              name="genres"
              selected={game.genres}
              onDelete={(v) => {
                deleteFromList(v, "genres");
              }}
            />
          ) : null}
          {errors.genres ? <p>{errors.genres}</p> : null}
        </label>
        <label>
          Select platforms for this game:
          <ListSelect
            name="platforms"
            list={platforms}
            onSelect={(v) => {
              handleList(v, "platforms");
            }}
          />
          {game.platforms ? (
            <SelectedList
              selected={game.platforms}
              onDelete={(v) => deleteFromList(v, "platforms")}
            />
          ) : null}
        </label>
        {errors.platforms ? <p>{errors.platforms}</p> : null}

        <input
          type="submit"
          onClick={(e) => submitCreation(e)}
          value="Create"
          disabled={errors ? true : false}
        />
      </form>
    </div>
  );
}
