import {
  GET_ALL_VIDEOGAMES,
  GET_ALL_GENRES,
  GET_GAME_BY_ID,
  GET_GAMES_BY_NAME,
  GET_ALL_PLATFORMS,
  FILTER_BY,
  SORT_GAMES,
  CREATE_GAME,
  RESET_FILTERS,
} from "./actions";
import { filter, sort } from "./utils";

const initialState = {
  games: [],
  gamesFiltered: [],
  genres: [],
  gameDetail: [],
  platforms: [],
  response: null,
};

export default function rootReducer(state = initialState, action) {
  switch (action.type) {
    case GET_ALL_VIDEOGAMES:
      return {
        ...state,
        games: action.payload,
        gamesFiltered: sort(action.payload, { param: "name", type: "ASC" }),
        gameDetail: [],
        response: null,
      };

    case GET_ALL_GENRES:
      return {
        ...state,
        genres: action.payload,
      };

    case GET_GAME_BY_ID:
      return {
        ...state,
        gameDetail: action.payload,
      };

    case GET_GAMES_BY_NAME:
      return {
        ...state,
        gamesFiltered: action.payload,
      };

    case GET_ALL_PLATFORMS:
      return {
        ...state,
        platforms: action.payload,
      };

    case FILTER_BY:
      const gamesToFilter = state.games;
      const gamesFiltered = filter(gamesToFilter, action.payload);
      if (!gamesFiltered.length) {
        return {
          ...state,
          gamesFiltered: gamesFiltered,
          response: "No results match the filters",
        };
      } else {
        return {
          ...state,
          gamesFiltered: filter(gamesToFilter, action.payload),
        };
      }

    case SORT_GAMES:
      const gamesToSort = state.gamesFiltered;
      return {
        ...state,
        gamesFiltered: sort(gamesToSort, action.payload),
      };

    case RESET_FILTERS:
      const filtersReseted = state.games;
      return {
        ...state,
        gamesFiltered: filtersReseted,
        response: null,
      };

    case CREATE_GAME:
      return {
        ...state,
        response: action.payload,
      };

    default:
      return state;
  }
}
