export function filter(games, values) {
  let filtered = [];
  if (values.genre === "All" && values.created === "All") {
    filtered = games;
  } else if (values.genre !== "All" && values.created === "All") {
    filtered = games.filter((g) => {
      if (g.created) {
        return g.genres.map((n) => n.name === values.genre);
      } else {
        return g.genres.includes(values.genre);
      }
    });
  } else if (values.genre === "All" && values.created !== "All") {
    if (values.created) {
      filtered = games.filter((g) => g.created === true);
    } else {
      filtered = games.filter((g) => g.created === false);
    }
  } else {
    filtered = games.filter((g) => {
      if (g.created === "true") {
        console.log("a:", typeof values.created);
        filtered = games.filter((g) => {
          return (
            g.genres.map((n) => n.name === values.genre) &&
            g.created === Boolean(values.created)
          );
        });
        console.log("b:", filtered);
      } else {
        filtered = games.filter((g) => {
          return (
            g.genres.includes(values.genre) &&
            g.created === Boolean(values.created)
          );
        });
      }
      return filtered;
    });
  }
  return filtered;
}
