import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useHistory } from "react-router-dom";
import { createGame, getAllGenres, getAllPlatforms } from "../../redux/actions";
import { getIds, validateForm } from "../common/utils";
import "../styles/createGame.css";
import ListSelect from "./Datalists";
import SelectedList from "./SelectedList";

export default function CreateGame() {
  const dispatch = useDispatch();
  const history = useHistory();
  const genres = useSelector((state) => state.genres);
  const platforms = useSelector((state) => state.platforms);
  const response = useSelector((state) => state.response);
  const [errors, setErrors] = useState({});
  const [inputPlatform, setInputPlatform] = useState("");
  const [inputGenres, setInputGenres] = useState("");
  const [genresList, setGenresList] = useState([]);
  const [disabled, setDisabled] = useState(true);
  const [game, setGame] = useState({
    name: "",
    description: "",
    released: "",
    rating: 0,
    image: "",
    platforms: [],
    genres: [],
  });

  useEffect(() => {
    dispatch(getAllGenres());
    dispatch(getAllPlatforms());
  }, [dispatch]);

  useEffect(() => {
    setErrors(validateForm(game));
  }, [game]);

  function handleChange(e) {
    const value = { ...game, [e.target.name]: e.target.value };
    setGame(value);
  }

  function handleGenresList(v) {
    const value = { ...game, genres: [...game.genres, v] };
    setGame(value);
    setInputGenres("");
  }
  function deleteFromList(v) {
    const newList = game.genres.filter((e) => e !== v);
    const value = { ...game, genres: newList };
    setGame(value);
  }

  function handlePlatformsList(v) {
    const value = { ...game, platforms: [...game.platforms, inputPlatform] };
    setGame(value);
    setInputPlatform("");
  }

  function deletePlatformList(e) {
    e.preventDefault();
    const newList = game.platforms.filter((p) => p !== e.target.value);
    setGame({ ...game, platforms: newList });
  }
  function submitCreation(e) {
    e.preventDefault();
    dispatch(createGame(game)).then(() => alert(response));
    history.push("/games");
  }

  return (
    <div>
      <form className="form">
        <label>
          Name
          <input type="text" name="name" onChange={(e) => handleChange(e)} />
          {errors.name ? <p>{errors.name}</p> : null}
        </label>
        <label>
          Description
          <input
            type="text"
            name="description"
            onChange={(e) => handleChange(e)}
          />
          {errors.description ? <p>{errors.description}</p> : null}
        </label>
        <label>
          Date of release
          <input
            type="date"
            name="released"
            onChange={(e) => handleChange(e)}
          />
          {errors.released ? <p>{errors.released}</p> : null}
        </label>
        <label>
          Rating
          <input
            type="number"
            name="rating"
            onChange={(e) => handleChange(e)}
          />
          {errors.rating ? <p>{errors.rating}</p> : null}
        </label>
        <label>
          Image:
          <input type="text" name="image" onChange={(e) => handleChange(e)} />
          {errors.image ? <p>{errors.image}</p> : null}
        </label>
        <label>
          Select genres for this game:
          <ListSelect
            name="genres"
            list={genres.map((g) => g.name)}
            onSelect={(v) => {
              handlePlatformsList(v);
            }}
          />
          {game.genres ? (
            <SelectedList
              selected={game.genres}
              onDelete={(v) => {
                deleteFromList(v);
              }}
            />
          ) : null}
          {errors.genres ? <p>{errors.genres}</p> : null}
        </label>
        <label>
          Select platforms for this game:
          <ListSelect
            name="platforms"
            list={platforms}
            onSelect={(v) => {
              handlePlatformsList(v);
            }}
          />
          {game.platforms ? (
            <SelectedList
              selected={game.platforms}
              onDelete={(v) => deleteFromList(v)}
            />
          ) : null}
        </label>
        {errors.platforms ? <p>{errors.platforms}</p> : null}

        <input
          type="submit"
          onClick={(e) => submitCreation(e)}
          value="Create"
          disabled={disabled}
        />
      </form>
    </div>
  );
}
