export function filter(games, values) {
  let filtered = [];

  if (values.genre === "All" && values.created === "All") {
    filtered = games;
  } else if (values.genre !== "All" && values.created === "All") {
    filtered = games.filter((g) => {
      if (g.created) {
        return g.genres.filter((n) => n.name === values.genre);
      } else {
        return g.genres.includes(values.genre);
      }
    });
  } else if (values.genre === "All" && values.created !== "All") {
    if (values.created === "db") {
      filtered = games.filter((g) => g.created === true);
    } else {
      filtered = games.filter((g) => g.created === false);
    }
  } else if (values.genre !== "All" && values.created !== "All") {
    if (values.created === "db") {
      filtered = [
        ...filtered,
        ...games.filter((g) => {
          return (
            g.genres.filter((n) => n.name === values.genre) &&
            g.created === true
          );
        }),
      ];
    } else {
      filtered = [
        ...filtered,
        games.filter((g, i = 1) => {
          return g.genres.includes(values.genre) && g.created === false;
        }),
      ];
    }
  }
  return filtered;
}
