import React from "react";
import "../components/styles/gameCard.css";
import { fixGenres } from "../redux/utils";

export default function GameCard({ name, image, genres }) {
  const genresFixed = fixGenres(genres);

  return (
    <>
      <h3 className="title">{name}</h3>
      <img src={image} alt="Not found" className="imgCard" />
      <h5 className="genres">{genresFixed}</h5>
    </>
  );
}
