import React from "react";

export default function Pagination({
  games,
  itemsPerPage,
  handlePage,
  currentPage,
}) {
  let pages = [];
  for (let i = 1; i < Math.ceil(games.length / itemsPerPage) + 1; i++) {
    pages.push(i);
  }

  return (
    <div>
      <button onClick={() => handlePage(currentPage - 1)}>{`<<`}</button>

      {pages.length
        ? pages.map((n) => {
            return (
              <button key={n} onClick={() => handlePage(n)}>
                {n}
              </button>
            );
          })
        : null}

      <button onClick={() => handlePage(currentPage + 1)}>{`>>`}</button>
    </div>
  );
}
