import React, { useState } from "react";
import { useEffect } from "react";
import { useDispatch } from "react-redux";
import { filterBy, sortGames } from "../../redux/actions";
import CustomSelect from "./CustomSelect";

export default function Filters({ genres, handlePage }) {
  const dispatch = useDispatch();
  const [filterValues, setFilterValues] = useState({
    genre: "All",
    created: "All",
  });
  const [sortValues, setSortValues] = useState({
    param: "name",
    type: "ASC",
  });
  useEffect(() => {
    dispatch(sortGames(sortValues));
  }, [sortValues, dispatch]);

  function handleSelectFilter(e) {
    const values = { ...filterValues, [e.target.name]: e.target.value };
    setFilterValues(values);
    dispatch(filterBy(values));
    dispatch(sortGames(sortValues));
    handlePage(1);
  }

  function handleSelectSort(e) {
    const fixedValues = e.target.value.split("/");
    const values = { param: fixedValues[0], type: fixedValues[1] };
    setSortValues(values);
    dispatch(sortGames(values));
    handlePage(1);
  }

  function handleResetFilters() {
    dispatch(resetFilters());
  }

  return (
    <div className="filters">
      <div>
        <div className="genresFilters">
          <CustomSelect
            name="genre"
            onSelect={handleSelectFilter}
            label="Filter games by genre:"
          >
            <option value="All" key="all">
              All
            </option>
            {genres.length
              ? genres.map((g) => (
                  <option key={g.id} value={g.name}>
                    {g.name}
                  </option>
                ))
              : null}
          </CustomSelect>
          <CustomSelect
            name="created"
            onSelect={handleSelectFilter}
            label={"Filter by source(api/db):"}
          >
            <option value="All">All</option>
            <option value="api">Api results</option>
            <option value="db">Db results</option>
          </CustomSelect>
        </div>
      </div>
      <div className="pipo">
        <CustomSelect
          name={"order"}
          onSelect={handleSelectSort}
          label={"Sort results by:"}
        >
          <option value="name/ASC">A-Z</option>
          <option value="name/DESC">Z-A</option>
          <option value="rating/DESC">Best rated</option>
          <option value="rating/ASC">Worst rated</option>
        </CustomSelect>
        <button onClick={handleResetFilters}>Reset Filters</button>
      </div>
    </div>
  );
}
