import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getAllGenres, getAllPlatforms } from "../../redux/actions";

export default function CreateGame() {
  const dispatch = useDispatch();
  const genres = useSelector((state) => state.genres);
  const platforms = useSelector((state) => state.platforms);
  const [genresInput, setGenresInput] = useState("");
  const [game, setGame] = useState({
    name: "",
    description: "",
    released: "",
    rating: 0,
    image: "",
    platforms: [],
    genres: [],
  });

  useEffect(() => {
    dispatch(getAllGenres());
    dispatch(getAllPlatforms());
  }, [dispatch]);

  function handleChange(e) {
    const value = { ...game, [e.target.name]: e.target.value };
    console.log(value);
    setGame(value);
  }

  function handleDatalistChange(e) {
    console.log(e.target.name);
    setGenresInput(e.target.value);
  }

  function handleSubmitDatalist(e) {
    e.preventDefault();
    setGame({ ...game, genres: [...game.genres, genresInput] });
  }

  return (
    <div>
      <form>
        <label>Name</label>
        <input type="text" name="name" onChange={(e) => handleChange(e)} />
        <label>Description</label>
        <input
          type="text"
          name="description"
          onChange={(e) => handleChange(e)}
        />
        <label>Date of release</label>
        <input type="date" name="released" onChange={(e) => handleChange(e)} />
        <label>Rating</label>
        <input type="number" name="rating" onChange={(e) => handleChange(e)} />
        <label>Image:</label>
        <input type="text" name="image" onChange={(e) => handleChange(e)} />
        <label>Select platforms for this game:</label>
        <input
          list="genres"
          name="genres"
          placeholder="Insert genres to filter"
          value={genresInput}
          onChange={(e) => handleDatalistChange(e)}
        />
        <datalist id="genres">
          {genres?.map((g) => {
            return (
              <option key={g.id} value={g.name} id={g.id}>
                {g.name}
              </option>
            );
          })}
        </datalist>
        <button onClick={handleSubmitDatalist}>Add</button>
      </form>
    </div>
  );
}
