export default function Pagination(games, itemsPerPage, handlePage){
 let pages = [];

 for(let i=1; i< Math.ceil(games / itemsPerPage); i++){
    console.log('loop')
    pages.push(i);
 }
console.log(pages)



return(
 <div>{
        pages.length? pages.map(n => {
        return(
        <button key={n} onClick={()=> handlePage(n)}>{n}</button>
        )}
        ) : null}

  </div>
 )
}
