import { GET_ALL_VIDEOGAMES, GET_ALL_GENRES, GET_GAME_BY_ID } from "./actions";

const initialState = {
  games: [],
  genres: [],
  gameDetail: [],
};

export default function rootReducer(state = initialState, action) {
  switch (action.type) {
    case GET_ALL_VIDEOGAMES:
      return {
        ...state,
        games: action.payload,
      };

    case GET_ALL_GENRES:
      return {
        ...state,
        genres: action.payload,
      };

    case GET_GAME_BY_ID:
      return {
        ...state,
        gameDetail: action.payload,
      };

    default:
      return state;
  }
}
