import {
  GET_ALL_VIDEOGAMES,
  GET_ALL_GENRES,
  GET_GAME_BY_ID,
  GET_GAMES_BY_NAME,
  GET_ALL_PLATFORMS,
  FILTER_BY,
} from "./actions";

const initialState = {
  games: [],
  gamesFiltered: [],
  genres: [],
  gameDetail: [],
  platforms: [],
};

export default function rootReducer(state = initialState, action) {
  switch (action.type) {
    case GET_ALL_VIDEOGAMES:
      return {
        ...state,
        games: action.payload,
        gameDetail: [],
      };

    case GET_ALL_GENRES:
      return {
        ...state,
        genres: action.payload,
      };

    case GET_GAME_BY_ID:
      return {
        ...state,
        gameDetail: action.payload,
      };

    case GET_GAMES_BY_NAME:
      return {
        ...state,
        games: action.payload,
      };

    case GET_ALL_PLATFORMS:
      return {
        ...state,
        platforms: action.payload,
      };

    case FILTER_BY:
      const games = state.games;
      console.log(action.payload);
      return {
        ...state,
        gamesFiltered: games,
      };

    default:
      return state;
  }
}
