import axios from "axios";

export const GET_ALL_VIDEOGAMES = "GET_ALL_VIDEOGAMES";
export const GET_ALL_GENRES = "GET_ALL_GENRES";
export const GET_GAME_BY_ID = "GET_GAME_BY_ID";
export const GET_GAMES_BY_NAME = "GET_GAMES_BY_NAME";
export const GET_ALL_PLATFORMS = "GET_ALL_PLATFORMS";
export const FILTER_BY = "FILTER_BY";
export const SORT_GAMES = "SORT_GAMES";
export const CREATE_GAME = "CREATE_GAME";
export const RESET_FILTERS = "RESET_FILTERS";

export function getAllVideogames() {
  return async function (dispatch) {
    const response = await axios.get(`http://localhost:3001/videogames`);
    return dispatch({
      type: GET_ALL_VIDEOGAMES,
      payload: response.data,
    });
  };
}

export function getAllGenres() {
  return async function (dispatch) {
    const response = await axios.get(`http://localhost:3001/genres`);
    return dispatch({
      type: GET_ALL_GENRES,
      payload: response.data,
    });
  };
}

export function getGameById(value) {
  const id = value.match.params.id;
  return async function (dispatch) {
    const response = await axios.get(`http://localhost:3001/videogames/${id}`);
    return dispatch({
      type: GET_GAME_BY_ID,
      payload: response.data,
    });
  };
}

export function getVideogamesByName(value) {
  return async function (dispatch) {
    const response = await axios.get(`http://localhost:3001/videogames`, {
      params: { search: value },
    });
    return dispatch({
      type: GET_GAMES_BY_NAME,
      payload: response.data,
    });
  };
}

export function getAllPlatforms() {
  return async function (dispatch) {
    const response = await axios.get(`http://localhost:3001/platforms`);
    return dispatch({
      type: GET_ALL_PLATFORMS,
      payload: response.data,
    });
  };
}

export function filterBy(values) {
  return function (dispatch) {
    return dispatch({
      type: FILTER_BY,
      payload: values,
    });
  };
}

export function sortGames(values) {
  return function (dispatch) {
    return dispatch({
      type: SORT_GAMES,
      payload: values,
    });
  };
}

export function createGame(values) {
  return async function (dispatch) {
    try {
      const response = await axios.post(`http://localhost:3001/videogames`, {
        name: values.name[0].toUpperCase() + values.name.slice(1).toLowerCase(),
        descriptions: values.description,
        released: values.released,
        rating: values.rating,
        image: values.image,
        platforms: values.platforms,
        genres: values.genres,
      });
      return dispatch({
        type: CREATE_GAME,
        payload: response.data,
      });
    } catch (error) {
      return dispatch({
        type: CREATE_GAME,
        payload: error.message,
      });
    }
  };
}

export function resetFilters() {
  return dispatch({
    type: RESET_FILTERS,
    payload,
  });
}
