export function filter(games, values) {
  let gamesFiltered = [];
  if (values.genre === "All" && values.created === "All") {
    gamesFiltered = games;
  } else if (values.genre !== "All" && values.created === "All") {
    gamesFiltered = games.filter((g) => g.genre.includes(values.genre));
  } else if (values.genre === "All" && values.created !== "All") {
    if (values.created === "api") {
      gamesFiltered = games.filter((g) => {
        g.created === false;
      });
    } else {
      gamesFiltered = games.filter((g) => {
        g.created === true ? g : null;
      });
    }
  }
  return gamesFiltered;
}
