import React from "react";


export default function Pagination({games, itemsPerPage, handlePage, currentPage}){
 let pages = [];
 for(let i=1; i < Math.ceil(Object.keys(games).length / itemsPerPage)+1; i++){
    pages.push(i);
 }
// currentPage !== 1 ?
// currentPage !== Math.ceil(Object.keys(games).length / itemsPerPage) ?

return(
 <div>
    <button onClick={()=>handlePage(currentPage-1)}></button>

    { pages.length? pages.map(n => {
         return(
        <button key={n} onClick={()=> handlePage(n)}>{n}</button>
        )}
        ) : null}

    <button onClick={()=>handlePage(currentPage+1)}>N</button>        
  </div>
 )
}
