import React from "react";
import { useDispatch } from "react-redux";
import { filterBy } from "../redux/actions";

export default function Filters({ genres }) {
  const [filterGames, setFilterGames] = {
    genre: "",
    created: "",
  };
  return (
    <div>
      <select>
        {genres.length
          ? genres.map((g) => {
              return <option key={g.id}>{g.name}</option>;
            })
          : null}
      </select>
      <select>
        <option value="api">Api results</option>
        <option value="db">Db results</option>
      </select>
    </div>
  );
}
