import React, {useEffect, useState} from "react";
import { useDispatch, useSelector} from 'react-redux'
import { getAllGenres, getAllVideogames } from "../redux/actions";
import GameCards from "./GameCards";
import Loading from "./Loading";
import Pagination from "./Pagination";
import SearchBar from "./SearchBar";

export default function Games(){
    const dispatch = useDispatch();
    const games = useSelector((state)=> state.games);
    const [currentPage, setCurrentPage] = useState(1);
    const [itemsPerPage, setItemsPerPage] = useState(15);
    const indexOfFirstItem = (currentPage - 1) * itemsPerPage;
    const indexOfLastItem = indexOfFirstItem + itemsPerPage;
    const currentList = games.slice(indexOfFirstItem, indexOfLastItem)


    useEffect(()=>{
        dispatch(getAllVideogames())
        dispatch(getAllGenres())
    }, [dispatch])


    function handlePage(n){
        setCurrentPage(n)
    }

    return (
        <div>
            {currentList.length? 
            <div>
                <SearchBar/>
                <Pagination games={games} itemsPerPage={itemsPerPage} handlePage={handlePage} />
                <GameCards games={currentList}/> 
            </div>: 
            <Loading/>}
        </div>
    )
}
