import { useState } from "react";
import "./customDatalist.css";

export default function CustomDatalist({ name, list, onSelect, selectedList }) {
  const [input, setInput] = useState("");
  function handleInput(e) {
    setInput(e.target.value);
  }

  function handleSubmit() {
    if (list.includes(input)) {
      onSelect(input);
      console.log(input);
    }
    setInput("");
  }
  // const filter = list.filter((e) => !selectedList.find((e2) => e === e2));
  const filter2 =
    name === "genres"
      ? list.filter((e) => !selectedList.find((g) => g === e))
      : list.filter((e) => selectedList.find((g) => g === e));
  const renderList = filter2.length > 0 ? filter2 : list;

  return (
    <div className="customDatalist">
      <input
        list={name}
        name={name}
        placeholder={`Type here to filter ${name}`}
        value={input}
        onChange={(e) => handleInput(e)}
        className="inputSelect"
      />
      <datalist id={name}>
        {renderList.map((e) => {
          return (
            <option key={e} value={e}>
              {e}
            </option>
          );
        })}
      </datalist>
      <button type="button" onClick={handleSubmit} className="datalistButton">
        +
      </button>
    </div>
  );
}
