import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getAllGenres, getAllPlatforms } from "../../redux/actions";

export default function CreateGame() {
  const dispatch = useDispatch();
  const genres = useSelector((state) => state.genres);
  const platforms = useSelector((state) => state.platforms);
  const [inputPlatform, setInputPlatform] = useState("");
  const [game, setGame] = useState({
    name: "",
    description: "",
    released: "",
    rating: 0,
    image: "",
    platforms: [],
    genres: [],
  });

  useEffect(() => {
    dispatch(getAllGenres());
    dispatch(getAllPlatforms());
  }, [dispatch]);

  function handleChange(e) {
    const value = { ...game, [e.target.name]: e.target.value };
    setGame(value);
  }

  function handleGenreSelect(e) {
    const value = { ...game, genres: [...game.genres, e.target.value] };
    setGame(value);
  }

  function handleInputPlatformsChange(e) {
    setInputPlatform(e.target.value);
  }

  function handlePlatformsList(e) {
    e.preventDefault();
    setGame({ ...game, platforms: [...game.platforms, inputPlatform] });
    setInputPlatform("");
  }

  return (
    <div>
      <form>
        <label>Name</label>
        <input type="text" name="name" onChange={(e) => handleChange(e)} />
        <label>Description</label>
        <input
          type="text"
          name="description"
          onChange={(e) => handleChange(e)}
        />
        <label>Date of release</label>
        <input type="date" name="released" onChange={(e) => handleChange(e)} />
        <label>Rating</label>
        <input type="number" name="rating" onChange={(e) => handleChange(e)} />
        <label>Image:</label>
        <input type="text" name="image" onChange={(e) => handleChange(e)} />
        <label>Select genres for this game:</label>
        <select defaultValue={"Select"}>
          {genres
            ?.filter((v) => !game.genres.find((v2) => v2.id === v.id))
            .map((g) => {
              return (
                <option
                  key={g.id}
                  value={g.id}
                  onClick={(e) => handleGenreSelect(e)}
                >
                  {g.name}
                </option>
              );
            })}
        </select>
        <label>
          Select platforms for this game:
          <input
            list="platforms"
            name="platforms"
            placeholder="Type here to filter platforms"
            onChange={handleInputPlatformsChange}
          />
        </label>
        <datalist id="platforms">
          {platforms?.map((p) => {
            return <option value={p} key={p} />;
          })}
        </datalist>
        <button onClick={(e) => handlePlatformsList(e)}>+</button>
      </form>
    </div>
  );
}
