import React from "react";
import "../components/styles/gameCard.css";

export default function GameCard({ name, image, genres, created }) {
  return (
    <>
      <h3 className="title">{name}</h3>
      <img src={image} alt="Not found" className="imgCard" />
      <h5 className="genres">
        {created
          ? genres.map((g, i) =>
              i === genres.length - 1 ? g.name : g.name + ", "
            )
          : genres.map((g, i) => (i === genres.length - 1 ? g : g + ", "))}
      </h5>
    </>
  );
}
